mapper.select:
select review_date, content, good, grade, user_id, menu_id, sweet, bitter, sour 
from review
where menu_id=#{menu_id}

select review_date, content, good, grade, user_id, menu_id, sweet, bitter, sour 
from review
where user_id=#{user_id}

mapper.update:
update review 
set good=good+1 
where menu_id=#{menuId} and user_id=#{userId}
