mapper.update:
update review 
set good=good+1 
where review_id=#{reviewId}

mapper.select:
select review_id, review_date, content, good, grade, user_id, menu_id, sweet, bitter, sour 
from review
where menu_id=#{menuId} and user_id=#{userId}

select review_id, review_date, content, good, grade, user_id, menu_id, sweet, bitter, sour 
from review
where review_id=#{reviewId}
